        // https://leetcode.com/contest/biweekly-contest-31/problems/count-odd-numbers-in-an-interval-range/
        // 1523. Count Odd Numbers in an Interval Range
        public int CountOdds(int low, int high)
        {
            int count = (high - low) / 2;
            if (high % 2 != 0 || low % 2 != 0)
            {
                count++;
            }

            return count;
        }

