        // O(N^2)
        public int NumOfSubarrays3(int[] arr)
        {
            int countOfOdd = 0;
            for (int i = 0; i < arr.Length; i++)
            {
                if (arr[i] % 2 != 0)
                {
                    countOfOdd = (countOfOdd + 1) % 1000000007;
                }

                long sum = arr[i];
                int z = i + 2;
                for (int j = i + 1; j < arr.Length; j = j + 2)
                {
                    sum += arr[j];
                    if (sum % 2 != 0)
                    {
                        countOfOdd = (countOfOdd + 1) % 1000000007;
                    }

                    if (z >= arr.Length)
                    {
                        continue;
                    }

                    sum += arr[z];
                    if (sum % 2 != 0)
                    {
                        countOfOdd = (countOfOdd + 1) % 1000000007;
                    }

                    z = z + 2;
                }
            }

            return countOfOdd;
        }

