        // Number of Sub-arrays With Odd Sum
        // O(N)
        public int NumOfSubarrays(int[] arr)
        {
            int mod = 1000000007;
            int even = 1;
            int odd = 0;
            int sum = 0;
            int result = 0;
            for (int i = 0; i < arr.Length; i++)
            {
                sum += arr[i];
                if (sum % 2 == 0)
                {
                    result += odd;
                    even++;
                }
                else
                {
                    result += even;
                    odd++;
                }

                result %= mod;
            }

            return result;
        }
    }
